import './App.css';

function App() {


  return (
    <div className="container-fluid">
    <div className="App">
      <h2> Calculadora de índice de massa corporal</h2>

    </div>
    <div className="main">
    <form onSubmit={calcular}>

      <h4> Digite a altura </h4>
      <input type="text" id="txt-altura" placeholder="Altura"/>
      <h4> Digite o peso </h4>
      <input type="text" id='txt-peso' placeholder="Peso"/>

      <br></br><br></br>

      <input type="submit" value="calcular"/>
      <br></br><br></br>

      <h4>índice</h4>
      <input type="text" id='result' placeholder='indice'/><br></br><br></br>
      <h4>classificação</h4>
      <input type="text" id='result-class'/>
      </form>

      </div>

      </div>

  );

  function calcular(e){

    e.preventDefault();
    var altura =parseFloat(document.getElementById("txt-altura").value);
    var peso = parseFloat(document.getElementById("txt-peso").value);

    var indice = (peso/(altura*altura));

    document.getElementById("result").value = indice.toFixed(2);
    console.log(indice);

    switch (indice) {
      case (indice > 18.5 && indice < 24.9):document.getElementById("result-class").value = "Normal";
      break;

     }



  }


}

export default App;
